arguments.argument:
/// Draw Sparkle.

// Get and Set Alpha:
var my_alpha;
if (instance_exists(obj_player))
{
    my_alpha = obj_player.animation_alpha;
}
else
{
    my_alpha = 0;
}

// Draw the Sparkle.
draw_sprite_ext(sprite_index, image_index, x + lengthdir_x(r, d), y + lengthdir_y(r, d), 1, 1, 0, c_white, my_alpha);
