arguments.argument:
/// Change the spring's frame.
if (spring_frame != 0 && spring_frametime != 0)
{
    if (spring_frametime != 0)
    {
        spring_frametime -= 1;
    }

    if (spring_frametime == 5)
    {
        spring_frame = 1;
    }
    else if (spring_frametime == 0)
    {
        spring_frame     = 0;
        spring_frametime = 0;
    }
}
