arguments.argument:
/// Animation and depth.

// Animation:
if (handle.shield_state == 0)
{
    image_speed = 0.5;
}
if (handle.shield_state == 1)
{
    image_speed = 0;
}
if (handle.shield_state == 2)
{
    image_speed = 0.25;
    if (image_index > 5)
    {
        handle.shield_state = 0;
        sprite_index        = spr_shield_bubble;
    }
}

// Set depth:
depth = -20;
